// Audio management
const bgm = new Audio();
bgm.src = "assets/bgm.mp3";
bgm.loop = true;
bgm.volume = 0.5;

let isMuted = false;
let lastGameState = "start"

const audioToggleBtn = document.querySelector("#audio-toggle-btn")

// Toggle mute state
audioToggleBtn.addEventListener("click", () => {
  isMuted = !isMuted
  if (isMuted) {
    bgm.pause()
    audioToggleBtn.textContent = "🔇"
  } else {
    if (getGameState() === "playing") {
      bgm.play()
    }
    audioToggleBtn.textContent = "🔊"
  }
})

// Monitor game state and control music
setInterval(() => {
  const currentState = getGameState()

  // State changed to playing
  if (lastGameState !== "playing" && currentState === "playing") {
    if (!isMuted) {
      bgm.play()
    }
  }

  // State changed from playing
  if (lastGameState === "playing" && currentState !== "playing") {
    bgm.pause()
    bgm.currentTime = 0
  }

  lastGameState = currentState
}, 100)

